INSERT INTO `gamedetail` VALUES ('14', '1', 'Windows       macOS', '《茶杯头》是一款以BOSS对战为主的经典横版卷轴类射击游戏。受上世纪30年代动画片的启发，游戏无论是画面还是音效都努力再现了当时的技术，如传统的赛璐璐手绘动画、水彩背景还有原声爵士乐录音等。', 'Studio MDHR Entertainment Inc.', '14', 'https://media.st.dl.pinyuncloud.com/steam/apps/268910/capsule_184x69.jpg?t=1589281999', 'Cuphead', '68', 'Studio MDHR Entertainment Inc.', '0', '2017-09-28T16:00:00.000Z');
INSERT INTO `gamedetail` VALUES ('15', '1', 'Windows macOS SteamOS + Linux', 'Skul：The Hero Slayer是2D Rogue Lite动作平台游戏。负责魔王城平安的小骷髅“Skul”，为了拯救被人类捉住的魔王，一个人独自对抗帝国军队开始冒险。', 'SouthPAW Games', '15', 'https://media.st.dl.pinyuncloud.com/steam/apps/1147560/capsule_184x69.jpg?t=1638325664', '小骨：英雄杀手(Skul: The Hero Slayer)', '70', 'NEOWIZ', '0', '2021-01-20T16:00:00.000Z');
INSERT INTO `gamedetail` VALUES ('16', '1', 'Windows macOS SteamOS + Linux', 'Barotrauma是一款2D合作类舰艇模拟游戏，故事发生在太空中，带有生存恐怖元素。驾驶潜艇，发号施令，对战怪物，修复漏洞，操作机器，武装待命以及制作物品；同时保持警惕：Barotrauma中的危险不会自行暴露！', 'FakeFish, Undertow Games', '16', 'https://media.st.dl.pinyuncloud.com/steam/apps/602960/capsule_184x69_schinese.jpg?t=1637078428', 'Barotrauma 潜渊症', '89', 'Daedalic Entertainment', '0', '2019-06-05T16:00:00.000Z');
