INSERT INTO `gamedetail` VALUES ('40', '1', 'Windows macOS SteamOS + Linux', '《Desperados III》是一款设定在无情西部荒野，剧情丰富的硬核战术潜行游戏。开动脑筋才能取得成功。能否拥有完善的计划，将决定你能生存下来，还是被枪口指着脑袋。', 'Mimimi Games', '40', 'https://media.st.dl.pinyuncloud.com/steam/apps/610370/capsule_184x69.jpg?t=1631811822', 'Desperados III', '259', 'THQ Nordic', '0', '2020-06-16T16:00:00.000Z');
