INSERT INTO `gamedetail` VALUES ('128', '2', ' 64-bit Windows 7, Windows 8.1, Windows 10 SteamOS+Linux', 'Hobo: Tough Life is an urban survival role-playing game where you play as a homeless person. Play alone or team up in online co-op. Explore the streets of Praslav, scavenge for food and supplies, beg, steal, and do whatever else it takes to survive the forthcoming winter.', 'Perun Creative', '128', 'https://media.st.dl.pinyuncloud.com/steam/apps/632300/capsule_184x69.jpg?t=1635953927', 'Hobo: Tough Life', '80', 'Perun Creative', '0', '2021-04-12T16:00:00.000Z');
