INSERT INTO `gamedetail` VALUES ('146', '3', 'Windows,macOS,SteamOS + Linux', '屡获殊荣的微策略游戏系列推出了最新的升级版，在这里经营并建设您自己的王国，保护它免受贪婪生物的威胁！在全新的单人或合作战役模式里，体验新的科技、部队、敌人、坐骑和隐藏要素。', 'Stumpy??Squid,Fury Studios,Coatsink', '146', 'https://media.st.dl.pinyuncloud.com/steam/apps/701160/capsule_184x69_alt_assets_10.jpg?t=1640004207', '王国：两位君主', '70', 'Raw Fury', '0', '2018-12-10T16:00:00.000Z');
