INSERT INTO `gamedetail` VALUES ('151', '3', 'Windows,macOS', '这款游戏的灵感来源于《伊利亚特》，由屡获殊荣的战略游戏系列带入现实，它将宏大的回合制帝国管理和壮观的实时战斗合为一体，直达特洛伊战争的核心。', 'CREATIVE ASSEMBLY,Feral Interactive (Mac)', '151', 'https://media.st.dl.pinyuncloud.com/steam/apps/1099410/capsule_184x69.jpg?t=1639359981', 'A Total War Saga: TROY', '223', 'SEGA, Feral Interactive (Mac)', '0', '2021-09-01T16:00:00.000Z');
