INSERT INTO `products` VALUES ('策略类游戏', 'Windows XP / Vista / 7 / 8 / 10 32 or 64 bit', '《泰坦之旅》发行十周年，绽放新辉煌。本次推出的十周年纪念版融合了《泰坦之旅》和《泰坦之旅：不朽王座》，给您带来焕然一新的终极动作角色扮演（ARPG）体验。', 'Iron Lore Entertainment,THQ Nordic', '110', 'https://media.st.dl.eccdnx.com/steam/apps/475150/capsule_184x69.jpg?t=1638867197', 'Titan Quest Anniversary Edition', '62', 'THQ Nordic', '231', '2016-08-31T16:00:00.000Z', '138', '1', '34');
INSERT INTO `products` VALUES ('策略类游戏', 'Windows 7+ 64bit macOS SteamOS++Linux', '《开拓者：拥王者》是第一款奠基于《开拓者》奇幻世界设定、采用等距视角且基于团队的电脑角色扮演游戏。透过这款受到《博得之门》、《辐射》1与2、《奥秘：蒸汽与魔法》等作品启发的游戏，尽情享受经典的角色扮演游戏体验。探索并征服失窃之地，将它们纳入你的王国！', 'Owlcat Games', '111', 'https://media.st.dl.eccdnx.com/steam/apps/640820/capsule_184x69.jpg?t=1640185221', 'Pathfinder: Kingmaker - Enhanced Plus Edition', '26', 'Prime Matter', '124', '2018-09-24T16:00:00.000Z', '302', '1', '34');
