INSERT INTO `products` VALUES ('策略类游戏', 'Windows 7+ 64bit', '在迷人的波西亚小镇开始新的生活！通过完成订单，种植庄稼，饲养动物，以及和这个迷人的后启示录土地上的古怪居民成为朋友，让你父亲疏于照料的工坊恢复昔日的辉煌!', 'Pathea Games', '113', 'https://media.st.dl.eccdnx.com/steam/apps/666140/capsule_184x69_schinese.jpg?t=1636724347', '波西亚时光', '328', 'Team17', '570', '2019-01-14T16:00:00.000Z', '496', '1', '12');
INSERT INTO `products` VALUES ('策略类游戏', 'Windows? 7, 64-bit macOS SteamOS+Linux', '外星人统治着地球，为人类许诺了一个美好的未来，但却在暗中隐藏着一个阴险的计划。作为面对着重重困难的游击势力领袖，您必须点燃全球的反抗之火消灭外星威胁并阻止人类灭绝。', 'Firaxis Games,Feral Interactive (Mac),Feral Interactive (Linux)', '114', 'https://media.st.dl.eccdnx.com/steam/apps/268500/capsule_184x69.jpg?t=1639065813', 'XCOM? 2', '70', '2K,Feral Interactive (Mac),Feral Interactive (Linux)', '749', '2016-02-04T16:00:00.000Z', '236', '1', '34');
