INSERT INTO `products` VALUES ('动作类游戏', 'Windows,macOS', '来自曾开发过宝石迷阵和幻幻球的宝开公司的全新动作策略游戏！ 可怕的僵尸即将入侵你的家，唯一的防御方式就是你栽种的植物！武装你的植物，切换他们不同的功能，诸如强悍的豌豆射手或樱桃炸弹，更加快速有效的将僵尸阻挡在入侵的道路上。不同的敌人，不同的玩法构成五种不同的游戏模式，加之夕阳、浓雾以及泳池之类的障碍增加了其挑战性，奇特的游戏乐趣永无止境！ 独特的五种游戏模式：冒险，迷你，益智，生存，再加上毫无压力的禅境花园 多达 50 个的冒险模式关卡设定，从白天到夜晚再到浓雾，从房顶到游泳池，场景变化多样 游戏共有 26 种不同的僵尸敌人，包括撑杆僵尸，潜水僵尸和开着车子的雪橇车僵尸等，使游戏更具挑战性 49 种功能强大，互不相同的植物，并可收集硬币购买宠物蜗牛，升级道具等多种道具！', 'PopCap Games, Inc.', '142', 'https://media.st.dl.eccdnx.com/steam/apps/3590/capsule_184x69.jpg?t=1615390608', 'Plants vs. Zombies GOTY Edition', '32', 'PopCap Games, Inc., Electronic Arts', '341', '2009-05-05T16:00:00.000Z', '92', '1', '34');
