INSERT INTO `products` VALUES ('角色扮演类游戏', 'Windows SteamOS + Linux', '《爱子的选择》是大家期待已久的人气潜行策略游戏《影子战术》的独立扩展版，带领玩家来到日本江户时代，操控女忍者高手爱子和她的刺客朋友们，追捕她过去的幽灵。', 'Mimimi Games', '35', 'https://media.st.dl.eccdnx.com/steam/apps/1579380/capsule_184x69_alt_assets_0_schinese.jpg?t=1639432845', 'Shadow Tactics: 爱子的选择 Aiko\'s Choice', '298', 'Daedalic Entertainment', '372', '2021-12-06T16:00:00.000Z', '381', '1', '36');
