INSERT INTO `products` VALUES ('角色扮演类游戏', 'Windows SteamOS + Linux', 'X4: 基石，期待已久的，大获成功的X系列的续集把我们最精妙的宇宙模拟带给大家。以第一视角管理一个帝国或者探索宇宙。飞遍每种飞船、贸易以及战斗，还有模块化的空间站建设，去建立你的帝国，小心地走好史诗般旅途中的每一步。', 'Egosoft', '37', 'https://media.st.dl.eccdnx.com/steam/apps/392160/capsule_184x69.jpg?t=1637932891', 'X4: Foundations', '402', 'Egosoft', '259', '2018-11-29T16:00:00.000Z', '21', '1', '45');
