INSERT INTO `products` VALUES ('策略类游戏', 'Windows 7/8/10-64bit', '创造属于你的虚拟化身，在游戏原创剧情模式中与最强大的漫画英雄并肩作战，或是前去联机大厅挑战其他玩家，并且发掘各种玩法。', 'Spike Chunsoft Co., Ltd.', '74', 'https://media.st.dl.eccdnx.com/steam/apps/816020/capsule_184x69.jpg?t=1636520387', 'JUMP FORCE', '36', 'BANDAI NAMCO Entertainment', '815', '2019-02-14T16:00:00.000Z', '280', '1', '34');
