INSERT INTO `products` VALUES ('策略类游戏', 'Windows 7 SP1/8.1/10', '《ASSASSIN’S CREED? ORIGINS》是一次全新的开始 *《Assassin’s Creed》Discovery Tour现已免费更新！* 王权和阴谋统治下的古埃及时代正慢慢消失在争权夺位的冷酷之战中。重返刺客兄弟会起源的最初时刻，揭开不为人知的秘密和被遗忘的神秘事件。', 'Ubisoft Montreal', '79', 'https://media.st.dl.eccdnx.com/steam/apps/582160/capsule_184x69.jpg?t=1603213581', 'Assassin\'s Creed? Origins', '268', 'Ubisoft', '730', '2017-10-26T16:00:00.000Z', '282', '1', '14');
INSERT INTO `products` VALUES ('角色扮演类游戏', 'Windows macOS SteamOS + Linux', '获奖作品 Total War: Warhammer的续集, Total War: Warhammer II将会在新世界给大家带来一次令人吃惊的探索、 扩张和征服旅程。', 'CREATIVE ASSEMBLY, Feral Interactive (Mac), Feral Interactive (Linux)', '8', 'https://media.st.dl.eccdnx.com/steam/apps/594570/capsule_184x69.jpg?t=1632304425', 'Total War: WARHAMMER II', '38', 'SEGA,Feral Interactive (Mac),Feral Interactive (Linux)', '347', '2017-09-27T16:00:00.000Z', '79', '1', '23');
