INSERT INTO `products-3` VALUES ('动作类游戏', 'Windows,macOS,SteamOS + Linux', '屡获殊荣的微策略游戏系列推出了最新的升级版，在这里经营并建设您自己的王国，保护它免受贪婪生物的威胁！在全新的单人或合作战役模式里，体验新的科技、部队、敌人、坐骑和隐藏要素。', 'Stumpy??Squid,Fury Studios,Coatsink', '146', 'https://media.st.dl.pinyuncloud.com/steam/apps/701160/capsule_184x69_alt_assets_10.jpg?t=1640004207', '王国：两位君主', '36', 'Raw Fury', '195', '2018-12-10T16:00:00.000Z', '149', '1', '34');
INSERT INTO `products-3` VALUES ('动作类游戏', 'Windows,macOS,SteamOS + Linux', '外星人统治着地球，为人类许诺了一个美好的未来，但却在暗中隐藏着一个阴险的计划。作为面对着重重困难的游击势力领袖，您必须点燃全球的反抗之火消灭外星威胁并阻止人类灭绝。', 'Firaxis Games,Feral Interactive (Mac),Feral Interactive (Linux)', '147', 'https://media.st.dl.pinyuncloud.com/steam/apps/268500/capsule_184x69.jpg?t=1639065813', 'XCOM? 2', '78', '2K, Feral Interactive (Mac), Feral Interactive (Linux)', '250', '2016-02-04T16:00:00.000Z', '471', '1', '34');
