INSERT INTO `products-3` VALUES ('动作类游戏', 'Windows,SteamOS + Linux', '《爱子的选择》是大家期待已久的人气潜行策略游戏《影子战术》的独立扩展版，带领玩家来到日本江户时代，操控女忍者高手爱子和她的刺客朋友们，追捕她过去的幽灵。', 'Mimimi Games', '160', 'https://media.st.dl.pinyuncloud.com/steam/apps/1579380/capsule_184x69_schinese.jpg?t=1639432845', 'Shadow Tactics: 爱子的选择 Aiko\'s Choice', '21', 'Daedalic Entertainment', '172', '2021-12-06T16:00:00.000Z', '374', '1', '34');
