INSERT INTO `products-3` VALUES ('休闲类游戏', 'Windows,     macOS,SteamOS + Linux', 'Rento是一款线上多人联机考验智慧和运气的大富翁游戏，适合2-8个玩家的桌游。交易土地，建造房屋，赢得拍卖，以及惊险刺激大转盘，在俄罗斯轮盘里爽一把，开心才是最重要的。现在就和你的朋友在steam上对战吧 :)', 'LAN - GAMES LTD', '224', 'https://media.st.dl.pinyuncloud.com/steam/apps/663390/capsule_184x69.jpg?t=1635258516', 'Rento Fortune: Online Dice Board Game (大富翁)', '129', 'LAN - GAMES LTD', '733', '2017-07-13T16:00:00.000Z', '154', '1', '1');
