INSERT INTO `products-3` VALUES ('休闲类游戏', 'Windows,     macOS,SteamOS + Linux', '《怒之铁拳4》（又译：格斗五人组/格斗三人组4/格斗四人组4）是经典清版动作游戏《怒之铁拳》系列的最新作，该系列作品以不会过时的游戏机制与电子舞曲风的背景音乐而好评连连。4代在经典的游戏方式上，加入了新的游戏机制、精致的手绘特效与大师级音乐团队所创作的背景音乐。', 'Dotemu,Guard Crush,Lizardcube', '238', 'https://media.st.dl.pinyuncloud.com/steam/apps/985890/capsule_184x69_schinese.jpg?t=1626192580', '怒之铁拳4', '168', 'Dotemu,Yooreka Studio (Greater China)', '238', '2020-04-29T16:00:00.000Z', '430', '1', '14');
