INSERT INTO `products-3` VALUES ('角色扮演类游戏', 'Windows macOS', 'Hack, slash, and smash your way to victory in this award winning 2D arcade adventure from The Behemoth!', 'The Behemoth', '25', 'https://media.st.dl.pinyuncloud.com/steam/apps/204360/capsule_184x69_alt_assets_5.jpg?t=1640361794', 'Castle Crashers?', '18', 'The Behemoth', '757', '2012-09-26T16:00:00.000Z', '232', '1', '34');
INSERT INTO `products-3` VALUES ('休闲类游戏', 'Windows,     macOS,SteamOS + Linux', '《野餐大冒险》是一款奇妙的解谜探索游戏，故事发生在一个奇怪而有趣的世界里，但并不是一切都像看起来那么美好。帮助古怪的角色克服困难战胜挑战，揭开不可告人的国家阴谋，并在这次愉快的反乌托邦冒险中开始一场有趣的小革命！', 'Arnaud De Bock,Rémi Forcadell,Alan Zucconi,Calum Bowen', '250', 'https://media.st.dl.pinyuncloud.com/steam/apps/572890/capsule_184x69.jpg?t=1627314689', 'Pikuniku', '298', 'Devolver Digital', '819', '2019-01-24T16:00:00.000Z', '259', '1', '257');
INSERT INTO `products-3` VALUES ('休闲类游戏', 'Windows,     macOS', '葵和莎莉一個尋寶者，在探險途中發現了一個山洞。她们預感裡面有寶藏。所以决定進去看看。出乎意料的是，這是一個佈滿触手的洞穴。', 'Inverse Game', '251', 'https://media.st.dl.pinyuncloud.com/steam/apps/1205240/capsule_184x69.jpg?t=1601375471', 'Tentacle Girl', '138', 'Inverse Game', '176', '2020-02-13T16:00:00.000Z', '89', '1', '125');
INSERT INTO `products-3` VALUES ('休闲类游戏', 'Windows,     macOS', '鼠之城邦是一款使用卡牌来保卫城市，防御敌人攻击的游戏。特别的是，游戏具有即时战略的特性，所以为了防御即将到来的敌人，需要快速的判断力和爆发力。通过游戏获得并使用卡牌来建设您的鼠之城邦吧!', 'Cassel Games', '252', 'https://media.st.dl.pinyuncloud.com/steam/apps/1108370/capsule_184x69.jpg?t=1628062016', 'Ratropolis', '222', 'Cassel Games', '771', '2020-12-21T16:00:00.000Z', '154', '1', '364');
INSERT INTO `products-3` VALUES ('休闲类游戏', 'Windows,     macOS,SteamOS + Linux', '《夏天的一天》背景设定在 20 世纪 80 年代的香港。这款视觉小说讲述了两位女性之间的爱情故事。游戏的视觉效果活力明亮、色彩丰富。制作组从亚洲电影和 20 世纪 80 年代动画汲取灵感，为其绘制了超过 400 张独特的美术作品。', 'Oracle and Bone', '253', 'https://media.st.dl.pinyuncloud.com/steam/apps/1111370/capsule_184x69.jpg?t=1627879896', 'A Summer\'s End 夏天的一天 — 香港 1986', '69', 'Oracle and Bone', '75', '2020-04-22T16:00:00.000Z', '496', '1', '123');
