INSERT INTO `products-3` VALUES ('策略类游戏', 'Windows-64bit', '新的生命之地。狩猎, 就是本能! 在系列最新作品「Monster Hunter: World」中,玩家可以体验终极的狩猎生活,活用新建构的世界中各种各样的地形与生态环境享受狩猎的惊喜与兴奋。', 'CAPCOM Co., Ltd.', '59', 'https://media.st.dl.pinyuncloud.com/steam/apps/582010/capsule_184x69.jpg?t=1623978557', 'Monster Hunter: World', '54', 'CAPCOM Co., Ltd.', '836', '2018-08-09T16:00:00.000Z', '478', '1', '257');
INSERT INTO `products-3` VALUES ('角色扮演类游戏', 'Windows macOS SteamOS + Linux', '欢迎来到《死亡细胞》，这是一款将Roguelite与银河战士恶魔城类特点融为一炉的2D平台动作游戏。游戏中并无检查点，玩家将体验魂味战斗，一路挑战诸多守卫，在杀戮与死亡的反复轮回中探索一座房间不断变化的巨大城堡。', 'Motion Twin', '6', 'https://media.st.dl.pinyuncloud.com/steam/apps/588650/capsule_184x69.jpg?t=1631802497', 'Dead Cells', '48', 'Motion Twin', '161', '2018-08-06T16:00:00.000Z', '100', '1', '125');
INSERT INTO `products-3` VALUES ('策略类游戏', 'Windows macOS', '来自《博德之门3》的开发团队，倍受好评的本作提升了RPG业界标杆。召集你的队伍，驾驭富有深度和挑战性的战斗。以最多四人的队伍进行冒险 - 但是请记住，只有其中一人才能成神。', 'Larian Studios', '60', 'https://media.st.dl.pinyuncloud.com/steam/apps/435150/capsule_184x69.jpg?t=1631177562', 'Divinity: Original Sin 2 - Definitive Edition', '37', 'Larian Studios', '649', '2017-09-13T16:00:00.000Z', '52', '1', '364');
INSERT INTO `products-3` VALUES ('策略类游戏', 'Windows-64bit', '随着北方领域战乱四起，您接下了此生最为重大的一笔委托：找到预言之子，一件足以改变世界面貌的活生生的武器。', 'CD PROJEKT RED', '61', 'https://media.st.dl.pinyuncloud.com/steam/apps/292030/capsule_184x69_schinese.jpg?t=1621939214', '巫师 3：狂猎', '69', 'CD PROJEKT RED', '311', '2015-05-17T16:00:00.000Z', '453', '1', '123');
