INSERT INTO `products-3` VALUES ('策略类游戏', 'Window7+ 64bit macOS SteamOS+Linux', '在由备受好评的《开拓者：拥王者》的制作团队所推出的全新RPG史诗巨作中，踏上旅程，前往被恶魔肆虐的国度。探索善与恶的本质，了解力量的真正代价，并超凡飞升，成为能够行凡人所不能之事的神话英雄。', 'Owlcat Games', '63', 'https://media.st.dl.pinyuncloud.com/steam/apps/1184370/capsule_184x69.jpg?t=1638802973', 'Pathfinder: Wrath of the Righteous', '138', 'META Publishing,Owlcat Games', '794', '2021-09-02T16:00:00.000Z', '116', '1', '14');
