INSERT INTO `products-3` VALUES ('策略类游戏', 'Windows XP/Vista/7', 'Enter an apocalyptic fantasy world where humanity is on the brink of extinction, iron is valued above gold and trust is hard earned. This ARPG features complex character development, hundreds of unique items, crafting and quests with choice & consequence.', 'Crate Entertainment', '77', 'https://media.st.dl.pinyuncloud.com/steam/apps/219990/capsule_184x69.jpg?t=1639672339', 'Grim Dawn', '36', 'Crate Entertainment', '212', '2016-02-25T16:00:00.000Z', '302', '1', '123');
