INSERT INTO `products-3` VALUES ('策略类游戏', 'Windows 10 64-bit', '丧尸崛起，文明陷落。在这个动作策略模拟游戏中，您的一小队幸存者将不得不前往开放世界搜刮资源，并建立可以抵挡丧尸群攻击的基地。', 'Undead Labs', '99', 'https://media.st.dl.pinyuncloud.com/steam/apps/495420/capsule_184x69.jpg?t=1633102240', 'State of Decay 2: Juggernaut Edition', '160', 'Xbox Game Studios', '826', '2020-03-13T16:00:00.000Z', '427', '1', '123');
